package leetcode;

import java.util.HashMap;
import java.util.Map;

/**
 *  滑动窗口
 */
public class Solution567 {

    public static void main(String[] args) {
        Solution567 solution567 = new Solution567();
        String s1 = "";
        String s2 = "";

        boolean b = solution567.checkInclusion(s1,
                s2);
        System.out.println(b);
    }


    public boolean checkInclusion(String need, String str) {
        if (need.length() > str.length()) {
            return false;
        }

        Map<Character, Integer> needMap = new HashMap<>();
        for (int i = 0; i < need.length(); i++) {
            putCount(needMap, need.charAt(i));
        }

        Map<Character, Integer> window = new HashMap<>();

        int left = 0;
        int right = 0;
        int valid = 0;

        while (right < str.length()) {
            char r = str.charAt(right);
            right++;

            if (needMap.containsKey(r)) {
                int value = putCount(window, r);
                // 这里有个天大的坑， 找了一天bug才找出来，
                // 最开始的value是Integer类型的，然后用=号去比较，他妈的一直AC不了
                if (needMap.get(r) == value) {
                    valid++;
                }
            }

            while (right - left >= need.length()) {
                if (valid == needMap.size()) {
                    return true;
                }
                char l = str.charAt(left);
                left++;
                if (needMap.containsKey(l)) {
                    int value = removeCount(window, l);
                    if ((value + 1) == needMap.get(l)) {
                        valid--;
                    }
                }
            }

        }

        return false;
    }

    private Integer removeCount(Map<Character, Integer> map, char c) {
        Integer value = map.get(c);
        if (value == 1) {
            map.remove(c);
            return 0;
        } else {
            map.put(c, --value);
            return value;
        }
    }


    private Integer putCount(Map<Character, Integer> map, char c) {
        Integer value = map.get(c);
        if (value != null) {
            map.put(c, ++value);
            return value;
        } else {
            map.put(c, 1);
            return 1;
        }
    }
}
